import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import './index.css';

import {
    DeckList,
    SearchBar,
    SearchResults
} from './components';

import {
    fetchCards,
} from './api';

const App = () => {
    // fetchCards().then(console.log);
    const [results, setResults] = useState([]);
    const [deck, setDeck] = useState([]);
    console.log("new results", results)

    const addCardToDeck = ({ id, name }) => {
        const nextDeck = [...deck]; // make a duplicate first
        const index = nextDeck.findIndex(card => card.id === id);

        // index will be -1 if it is not found
        if (index > -1) {
          nextDeck[index].count += 1;
        } else {
          nextDeck.push({
            id,
            name,
            count: 1
          });
        }

        setDeck(nextDeck);
      }

    const removeCardFromDeck = ({ id }) => {
        // what goes here?
    }
    return (
        <div id="app">
            <SearchBar setResults={setResults} />
            <SearchResults
                results={results}
                addCardToDeck={addCardToDeck}
                removeCardFromDeck={removeCardFromDeck} />
            <DeckList deck={deck} />
        </div>
    );
}

ReactDOM.render(
    <App />,
    document.getElementById('root')
);
